                const int batch_idx = blockIdx.y;
                const int dim2_idx = blockIdx.x;
                const int tid = threadIdx.x;
                const int block_size = blockDim.x;

                // Calculate input base index for this batch and column
                const int input_base = batch_idx * dim1 * dim2 + dim2_idx;

                // Each thread computes partial sum with grid stride
                float thread_sum = 0.0f;

                // Process elements in chunks of 4 for better instruction-level parallelism
                const int step = block_size;
                int i = tid;

                // Main loop with 4-element unrolling
                // Precompute loop limit to avoid redundant calculations
                const int limit = dim1 - 3 * step;

                #pragma unroll 4
                for (; i <= limit; i += 4 * step) {
                    thread_sum += input[input_base + i * dim2];
                    thread_sum += input[input_base + (i + step) * dim2];
                    thread_sum += input[input_base + (i + 2 * step) * dim2];
                    thread_sum += input[input_base + (i + 3 * step) * dim2];
                }

                // Handle remaining elements
                for (; i < dim1; i += step) {
                    thread_sum += input[input_base + i * dim2];
                }

                // Shared memory for block-level reduction
                __shared__ float shared_data[256];

                // Store partial sum in shared memory
                shared_data[tid] = thread_sum;
                __syncthreads();

                // Block-level reduction in shared memory
                // Using sequential addressing to avoid bank conflicts
                #pragma unroll
                for (int s = block_size / 2; s > 32; s >>= 1) {
                    if (tid < s) {
                        shared_data[tid] += shared_data[tid + s];
                    }
                    __syncthreads();
                }

                // Last 64 elements reduced with warp-level operations (no sync needed within a warp)
                if (tid < 32) {
                    // For the last warp, we can use warp-level primitives
                    volatile float* smem = shared_data;
                    if (block_size >= 64) smem[tid] += smem[tid + 32];
                    if (block_size >= 32) smem[tid] += smem[tid + 16];
                    if (block_size >= 16) smem[tid] += smem[tid + 8];
                    if (block_size >= 8) smem[tid] += smem[tid + 4];
                    if (block_size >= 4) smem[tid] += smem[tid + 2];
                    if (block_size >= 2) smem[tid] += smem[tid + 1];

                    // First thread writes the result
