cuda_kernel_code = """
extern "C" __global__ void conv2d_kernel(
    const float* __restrict__ input,
    const float* __restrict__ weight,
    float* __restrict__ output,
    const float* __restrict__ bias,
    const int batch_size,
    const int in_channels,
    const int out_channels,
    const int in_height,
    const int in_width,
    const int out_height,
    const int out_width,
    const int kernel_h,
    const int kernel_w,
    const int stride,
    const int pad_h,
    const int pad_w,
    const int dilation_h,
    const int dilation_w) {

    // Calculate output position
    const int out_x = blockIdx.x * blockDim.x + threadIdx.x;
    const int out_y = blockIdx.y * blockDim.y + threadIdx.y;
    const int out_c = blockIdx.z % out_channels;
    const int b = blockIdx.z / out_channels;

    // Check if within output bounds
    if (out_x >= out_width || out_y >= out_height || b >= batch_size)
        return;

    // Compute input position based on output position
    const int in_y_start = out_y * stride - pad_h;
    const int in_x_start = out_x * stride - pad_w;

    // Accumulate result
    float result = 0.0f;

    // Iterate over input channels and kernel dimensions
    for (int in_c = 0; in_c < in_channels; ++in_c) {
        for (int k_y = 0; k_y < kernel_h; ++k_y) {
            const int in_y = in_y_start + k_y * dilation_h;

            // Skip if outside input height
            if (in_y < 0 || in_y >= in_height)
                continue;

            for (int k_x = 0; k_x < kernel_w; ++k_x) {
                const int in_x = in_x_start + k_x * dilation_w;

                // Skip if outside input width
                if (in_x < 0 || in_x >= in_width)
                    continue;

                // Get input value
                const float in_val = input[
                    ((b * in_channels + in_c) * in_height + in_y) * in_width + in_x
                ];

                // Get weight value
                const float w_val = weight[
                    ((out_c * in_channels + in_c) * kernel_h + k_y) * kernel_w + k_x
                ];

                // Accumulate
                result += in_val * w_val;
            }
        }
    }

    // Add bias if present
    if (bias != nullptr) {
        result += bias[out_c];
    }

    // Write to output
    output[
        ((b * out_channels + out_c) * out_height + out_y) * out_width + out_x
    ] = result;
}

// Optimized version with shared memory for input data
extern "C" __global__ void conv2d_kernel_shared(
    const float* __restrict__ input,
    const float* __restrict__ weight,
    float* __restrict__ output,
    const float* __restrict__ bias,
    const int batch_size,
    const int in_channels,
    const int out_channels,
    const int in_height,
    const int in_width,
    const int out_height,
    const int out_width,
    const int kernel_h,
    const int kernel_w,
    const int stride,
    const int pad_h,
    const int pad_w,
    const int dilation_h,
    const int dilation_w) {

    // Block dimensions
    const int TILE_WIDTH = blockDim.x;
    const int TILE_HEIGHT = blockDim.y;

    // Calculate output position
    const int out_x_base = blockIdx.x * TILE_WIDTH;
    const int out_y_base = blockIdx.y * TILE_HEIGHT;
    const int out_c = blockIdx.z % out_channels;
    const int b = blockIdx.z / out_channels;

    const int out_x = out_x_base + threadIdx.x;
    const int out_y = out_y_base + threadIdx.y;

    // Check if within batch bounds
    if (b >= batch_size)
        return;

    // Calculate the input tile size needed for this output tile
    const int in_x_start = out_x_base * stride - pad_w;
    const int in_y_start = out_y_base * stride - pad_h;

    // Calculate the size of the input tile needed
    const int in_tile_width = TILE_WIDTH * stride + (kernel_w - 1) * dilation_w;
    const int in_tile_height = TILE_HEIGHT * stride + (kernel_h - 1) * dilation_h;

    // Allocate shared memory for the input tile
    extern __shared__ float shared_mem[];

    // Accumulate result
    float result = 0.0f;

    // Iterate over input channels
    for (int in_c = 0; in_c < in_channels; ++in_c) {
        // Load input tile into shared memory
        for (int i = threadIdx.y; i < in_tile_height; i += TILE_HEIGHT) {
            int in_y = in_y_start + i;

            for (int j = threadIdx.x; j < in_tile_width; j += TILE_WIDTH) {
                int in_x = in_x_start + j;

                float val = 0.0f;
                if (in_y >= 0 && in_y < in_height && in_x >= 0 && in_x < in_width) {
                    val = input[((b * in_channels + in_c) * in_height + in_y) * in_width + in_x];
                }

                shared_mem[i * in_tile_width + j] = val;
            }
        }

        __syncthreads();

        // Check if within output bounds
        if (out_x < out_width && out_y < out_height) {
            // Compute convolution using shared memory
            for (int k_y = 0; k_y < kernel_h; ++k_y) {
                for (int k_x = 0; k_x < kernel_w; ++k_x) {
                    // Calculate position in the shared memory
                    const int sh_y = threadIdx.y * stride + k_y * dilation_h;
                    const int sh_x = threadIdx.x * stride + k_x * dilation_w;

                    if (sh_y >= 0 && sh_y < in_tile_height && sh_x >= 0 && sh_x < in_tile_width) {
                        const float in_val = shared_mem[sh_y * in_tile_width + sh_x];
                        const float w_val = weight[((out_c * in_channels + in_c) * kernel_h + k_y) * kernel_w + k_x];
                        result += in_val * w_val;
                    }
                }
            }
        }

        __syncthreads();
    }

    // Add bias and write output
    if (out_x < out_width && out_y < out_height) {
        if (bias != nullptr) {
            result += bias[out_c];
        }

        output[((b * out_channels + out_c) * out_height + out_y) * out_width + out_x] = result;
    }
}
