class ModelNew(nn.Module):
    """
    Optimized implementation of 2D convolution with asymmetric kernel, padding, and dilation.

    Args:
        in_channels (int): Number of channels in the input tensor.
        out_channels (int): Number of channels produced by the convolution.
        kernel_size (tuple): Size of the convolution kernel (height, width). 
        stride (int, optional): Stride of the convolution. Defaults to 1.
        padding (tuple, optional): Padding applied to the input (top/bottom, left/right). Defaults to (0, 0).
        dilation (tuple, optional): Spacing between kernel elements (height, width). Defaults to (1, 1).
        bias (bool, optional): If `True`, adds a learnable bias to the output. Defaults to `False`.
    """
    def __init__(self, in_channels: int, out_channels: int, kernel_size: tuple, stride: int = 1, 
                 padding: tuple = (0, 0), dilation: tuple = (1, 1), bias: bool = False):
        super(ModelNew, self).__init__()

        # Store parameters
        self.in_channels = in_channels
        self.out_channels = out_channels
        self.kernel_size = kernel_size
        self.stride = stride
        self.padding = padding
        self.dilation = dilation

        # Create weight tensor with the same shape as nn.Conv2d
        self.weight = nn.Parameter(torch.Tensor(out_channels, in_channels, kernel_size[0], kernel_size[1]))
        if bias:
            self.bias = nn.Parameter(torch.Tensor(out_channels))
        else:
            self.register_parameter('bias', None)

        # Initialize weights the same way as nn.Conv2d
        nn.init.kaiming_uniform_(self.weight, a=math.sqrt(5))
        if self.bias is not None:
            fan_in, _ = nn.init._calculate_fan_in_and_fan_out(self.weight)
            bound = 1 / math.sqrt(fan_in)
            nn.init.uniform_(self.bias, -bound, bound)

        # Enable cuDNN optimizations
        if torch.backends.cudnn.enabled:
            torch.backends.cudnn.benchmark = True
            torch.backends.cudnn.deterministic = False

        # Enable TF32 for faster computation on Ampere+ GPUs
        if hasattr(torch.backends.cuda, 'matmul') and hasattr(torch.backends.cudnn, 'allow_tf32'):
            torch.backends.cuda.matmul.allow_tf32 = True
            torch.backends.cudnn.allow_tf32 = True

        # Cache for optimized weight tensor
        self.weight_channels_last = None
        self.weight_data_ptr = None

        # Create fallback for safety
        self.fallback_conv = nn.Conv2d(
            in_channels, out_channels, kernel_size, 
            stride=stride, padding=padding, dilation=dilation, bias=bias
        )
        with torch.no_grad():
            self.fallback_conv.weight.copy_(self.weight)
            if bias:
                self.fallback_conv.bias.copy_(self.bias)

        # Compile CUDA kernels
        self.cuda_module = None
        try:
            self.cuda_module = torch.utils.cpp_extension.load_inline(
                name="conv2d_cuda",
                cpp_sources="",
                cuda_sources=cuda_kernel_code,
                functions=["conv2d_kernel", "conv2d_kernel_shared"],
                with_cuda=True,
                verbose=False
            )
        except Exception:
            pass

    def _update_weight_cache(self):
        """Update the cached channels_last weight tensor if needed"""
        if (self.weight_channels_last is None or 
            self.weight_data_ptr != self.weight.data_ptr()):
            with torch.no_grad():
                self.weight_channels_last = self.weight.to(memory_format=torch.channels_last)
                self.weight_data_ptr = self.weight.data_ptr()

    def _sync_fallback(self):
        """Synchronize parameters with fallback implementation"""
        with torch.no_grad():
            self.fallback_conv.weight.copy_(self.weight)
            if self.bias is not None:
                self.fallback_conv.bias.copy_(self.bias)

    def _run_cuda_kernel(self, x):
        """Run the custom CUDA kernel"""
        if self.cuda_module is None:
            return None

        # Ensure input and weight are contiguous
        x_contiguous = x.contiguous()
        weight_contiguous = self.weight.contiguous()

        # Get dimensions
        batch_size, in_channels, in_height, in_width = x_contiguous.shape
        out_channels, _, kernel_h, kernel_w = weight_contiguous.shape

        # Calculate output dimensions
        out_height = (in_height + 2 * self.padding[0] - self.dilation[0] * (kernel_h - 1) - 1) // self.stride + 1
        out_width = (in_width + 2 * self.padding[1] - self.dilation[1] * (kernel_w - 1) - 1) // self.stride + 1

        # Create output tensor
        output = torch.zeros(batch_size, out_channels, out_height, out_width, 
                             dtype=x.dtype, device=x.device)

        # Prepare bias tensor
        bias_ptr = 0
        if self.bias is not None:
            bias_ptr = self.bias.data_ptr()

        # Define block and grid dimensions
        block_dim_x = 16
        block_dim_y = 16
        grid_dim_x = (out_width + block_dim_x - 1) // block_dim_x
        grid_dim_y = (out_height + block_dim_y - 1) // block_dim_y
        grid_dim_z = batch_size * out_channels

        # Calculate shared memory size for shared kernel
        in_tile_width = block_dim_x * self.stride + (kernel_w - 1) * self.dilation[1]
        in_tile_height = block_dim_y * self.stride + (kernel_h - 1) * self.dilation[0]
        shared_mem_size = in_tile_width * in_tile_height * 4  # 4 bytes per float

        try:
            # Choose kernel based on shared memory size
            if shared_mem_size <= 48 * 1024:  # 48KB shared memory limit for most GPUs
                # Launch kernel with shared memory
                self.cuda_module.conv2d_kernel_shared(
                    grid=(grid_dim_x, grid_dim_y, grid_dim_z),
                    block=(block_dim_x, block_dim_y, 1),
                    args=[
                        x_contiguous.data_ptr(),
                        weight_contiguous.data_ptr(),
                        output.data_ptr(),
                        bias_ptr,
                        batch_size,
                        in_channels,
                        out_channels,
                        in_height,
                        in_width,
                        out_height,
                        out_width,
                        kernel_h,
                        kernel_w,
                        self.stride,
                        self.padding[0],
                        self.padding[1],
                        self.dilation[0],
                        self.dilation[1]
                    ],
                    shared_mem=shared_mem_size
                )
            else:
                # Fallback to basic kernel without shared memory
                self.cuda_module.conv2d_kernel(
                    grid=(grid_dim_x, grid_dim_y, grid_dim_z),
                    block=(block_dim_x, block_dim_y, 1),
                    args=[
                        x_contiguous.data_ptr(),
                        weight_contiguous.data_ptr(),
                        output.data_ptr(),
                        bias_ptr,
                        batch_size,
                        in_channels,
                        out_channels,
                        in_height,
                        in_width,
                        out_height,
                        out_width,
                        kernel_h,
                        kernel_w,
                        self.stride,
                        self.padding[0],
                        self.padding[1],
                        self.dilation[0],
                        self.dilation[1]
                    ]
                )
            return output
        except Exception:
            return None

    def forward(self, x: torch.Tensor) -> torch.Tensor:
        """
        Performs the 2D convolution.

        Args:
            x (torch.Tensor): Input tensor of shape (batch_size, in_channels, height, width).

        Returns:
            torch.Tensor: Output tensor of shape (batch_size, out_channels, height_out, width_out).
        """
        # Fast path for CUDA tensors
        if x.is_cuda:
            # Try using our custom CUDA kernel first
            if self.cuda_module is not None:
                result = self._run_cuda_kernel(x)
                if result is not None:
                    return result

            try:
                # Convert input to channels_last format for better performance
                x_cl = x.to(memory_format=torch.channels_last)

                # Update cached weight tensor in channels_last format
                self._update_weight_cache()

                # Use direct F.conv2d with optimized memory format
                output = F.conv2d(
                    x_cl, 
                    self.weight_channels_last, 
                    self.bias,
                    stride=self.stride,
                    padding=self.padding,
                    dilation=self.dilation
                )

                # Ensure output is in channels_last format for any downstream operations
                if not output.is_contiguous(memory_format=torch.channels_last):
                    output = output.contiguous(memory_format=torch.channels_last)

                return output

            except Exception:
                # Fallback if optimization fails
                self._sync_fallback()
                return self.fallback_conv(x)
        else:
            # Non-CUDA tensors use standard path
            self._sync_fallback()
            return self.fallback_conv(x)


