    // Launch kernel
    AT_DISPATCH_FLOATING_TYPES(input.scalar_type(), "depthwise_conv2d_kernel", ([&] {
        depthwise_conv2d_kernel<scalar_t><<<blocks, threads>>>(
            input.data_ptr<scalar_t>(),
            output.data_ptr<scalar_t>(),
            batch_size, channels, height, width,
            output_h, output_w
        );
    }));

    return output;
}
