cuda_source = """
#include <torch/extension.h>
#include <cuda.h>
#include <cuda_runtime.h>
#include <vector>

template <typename scalar_t>
__global__ void fused_conv_scale_min_kernel(
    const scalar_t* __restrict__ input,
    const scalar_t* __restrict__ weight,
    const scalar_t* __restrict__ bias,
    scalar_t* __restrict__ output,
    const int batch_size,
    const int in_channels,
    const int out_channels,
    const int in_height,
    const int in_width,
    const int kernel_size,
    const int out_height,
    const int out_width,
    const float scale_factor) {

    // Calculate output position
    const int w_out_base = blockIdx.x * blockDim.x + threadIdx.x;
    const int h_out = blockIdx.y * blockDim.y + threadIdx.y;
    const int b = blockIdx.z;

    // Thread indices
    const int tx = threadIdx.x;
    const int ty = threadIdx.y;
    const int tid = ty * blockDim.x + tx;
    const int block_size = blockDim.x * blockDim.y;

    // Define shared memory for weights
    extern __shared__ char shared_mem[];
    scalar_t* shared_weights = reinterpret_cast<scalar_t*>(shared_mem);

    // Collaboratively load weights into shared memory
    const int weight_size = out_channels * in_channels * kernel_size * kernel_size;
    const int weights_per_thread = (weight_size + block_size - 1) / block_size;

    #pragma unroll
    for (int i = 0; i < weights_per_thread; ++i) {
        const int idx = tid + i * block_size;
        if (idx < weight_size) {
            // Pre-scale weights during loading
            shared_weights[idx] = weight[idx] * scale_factor;
        }
    }

    __syncthreads();

    // Process multiple output pixels per thread for better arithmetic intensity
    // Each thread processes pixels along the width dimension with stride equal to blockDim.x
    for (int w_offset = 0; w_offset < 16 && w_out_base + w_offset < out_width; w_offset += 4) {
        const int w_out = w_out_base + w_offset;

        // Only proceed if this position corresponds to a valid output pixel
        if (h_out < out_height) {
            // Initialize minimum value to a large number
            scalar_t min_val = 1e10f;

            // Process all output channels
            for (int oc = 0; oc < out_channels; ++oc) {
                // Initialize convolution result with bias if available
                scalar_t conv_result = bias != nullptr ? bias[oc] * scale_factor : 0.0f;

                // Compute convolution for this output pixel and channel
                for (int ic = 0; ic < in_channels; ++ic) {
                    const int weight_offset = ((oc * in_channels) + ic) * kernel_size * kernel_size;

                    // Unroll inner loops for better instruction-level parallelism
                    #pragma unroll
                    for (int kh = 0; kh < kernel_size; ++kh) {
                        const int h_in = h_out + kh;

                        #pragma unroll
                        for (int kw = 0; kw < kernel_size; ++kw) {
                            const int w_in = w_out + kw;

                            if (h_in < in_height && w_in < in_width) {
                                const int input_idx = ((b * in_channels + ic) * in_height + h_in) * in_width + w_in;
                                const int weight_idx = weight_offset + kh * kernel_size + kw;

                                conv_result += input[input_idx] * shared_weights[weight_idx];
                            }
                        }
                    }
                }

                // Update minimum value
                min_val = min(min_val, conv_result);
            }

            // Write result to output
            const int output_idx = (b * out_height + h_out) * out_width + w_out;
            output[output_idx] = min_val;
        }
    }
}

std::vector<torch::Tensor> fused_conv_scale_min_cuda(
    torch::Tensor input,
    torch::Tensor weight,
    torch::Tensor bias,
    float scale_factor) {

    const auto batch_size = input.size(0);
    const auto in_channels = input.size(1);
    const auto in_height = input.size(2);
    const auto in_width = input.size(3);
    const auto out_channels = weight.size(0);
    const auto kernel_size = weight.size(2);

    const auto out_height = in_height - kernel_size + 1;
    const auto out_width = in_width - kernel_size + 1;

    auto output = torch::zeros({batch_size, 1, out_height, out_width}, 
                              input.options());

    // Optimized thread block configuration
    const dim3 threads(32, 8);
    const dim3 blocks((out_width + threads.x - 1) / threads.x,
                     (out_height + threads.y - 1) / threads.y,
                     batch_size);

    // Calculate shared memory size for weights
    const int shared_mem_size = out_channels * in_channels * kernel_size * kernel_size * sizeof(float);

    AT_DISPATCH_FLOATING_TYPES(input.scalar_type(), "fused_conv_scale_min_kernel", ([&] {
        fused_conv_scale_min_kernel<scalar_t><<<blocks, threads, shared_mem_size>>>(
            input.data_ptr<scalar_t>(),
            weight.data_ptr<scalar_t>(),
            bias.defined() ? bias.data_ptr<scalar_t>() : nullptr,
            output.data_ptr<scalar_t>(),
            batch_size,
            in_channels,
            out_channels,
            in_height,
            in_width,
            kernel_size,
            out_height,
            out_width,
            scale_factor);
