        else:
            # Use PyTorch's optimized implementation
            x = F.linear(x, self.weight, self.bias_linear)
            x = F.silu(x, inplace=True)

        # Add bias in-place to reduce memory allocation
        x.add_(self.bias)

        # Apply group normalization
        x = self.group_norm(x)

        return x

