        scalar_t softplus_val = stable_softplus(acc);
        scalar_t tanh_val = fast_tanh(softplus_val);
        scalar_t act_result = tanh_val * acc;

        // Apply batch normalization
        scalar_t bn_result = gamma * (act_result - mean) * inv_std + beta;

        // Write output
