                    const scalar_t sqrt_2_over_pi = 0.7978845608028654;
                    const scalar_t coef = 0.044715;
                    scalar_t x2 = x * x;
                    scalar_t x3 = x2 * x;
                    return 0.5f * x * (1.0f + tanhf(sqrt_2_over_pi * (x + coef * x3)));
