cuda_source = """
#include <torch/extension.h>
#include <cuda.h>
#include <cuda_runtime.h>
#include <vector>

// Helper function for warp-level reduction
template <typename scalar_t>
__device__ __forceinline__ scalar_t warpReduceSum(scalar_t val) {
    for (int offset = 16; offset > 0; offset /= 2)
        val += __shfl_down_sync(0xffffffff, val, offset);
    return val;
}

// Helper function for warp-level min reduction
template <typename scalar_t>
__device__ __forceinline__ scalar_t warpReduceMin(scalar_t val) {
    for (int offset = 16; offset > 0; offset /= 2)
        val = min(val, __shfl_down_sync(0xffffffff, val, offset));
    return val;
}

// Vectorized load for better memory bandwidth utilization
template <typename scalar_t>
__device__ __forceinline__ void loadVector4(const scalar_t* src, scalar_t* dst, int idx) {
    if constexpr (std::is_same<scalar_t, float>::value) {
        float4 tmp = *reinterpret_cast<const float4*>(&src[idx]);
        dst[0] = tmp.x;
        dst[1] = tmp.y;
        dst[2] = tmp.z;
        dst[3] = tmp.w;
    } else {
        #pragma unroll
        for (int i = 0; i < 4; ++i) {
            dst[i] = src[idx + i];
        }
    }
}

template <typename scalar_t>
__global__ void gemm_groupnorm_min_bias_kernel(
    const scalar_t* __restrict__ input,
    const scalar_t* __restrict__ weight,
    const scalar_t* __restrict__ bias,
    const scalar_t* __restrict__ gamma,
    const scalar_t* __restrict__ beta,
    const scalar_t* __restrict__ final_bias,
    scalar_t* __restrict__ output,
    int batch_size,
    int in_features,
    int out_features,
    int num_groups,
    float eps) {

    // Each thread block handles one batch element and one group
    int batch_idx = blockIdx.x;
    int group_idx = blockIdx.y;
    int tid = threadIdx.x;
    int lane_id = tid % 32;
    int warp_id = tid / 32;

    int features_per_group = out_features / num_groups;
    int group_start = group_idx * features_per_group;

    // Shared memory for partial sums and intermediate results
    extern __shared__ char shared_mem_char[];
    scalar_t* shared_mem = reinterpret_cast<scalar_t*>(shared_mem_char);
    scalar_t* group_sum = shared_mem;
    scalar_t* group_sum_sq = &shared_mem[blockDim.x];
    scalar_t* group_output = &shared_mem[2 * blockDim.x];

    // Initialize shared memory
    group_sum[tid] = 0.0f;
    group_sum_sq[tid] = 0.0f;

    // Step 1: GEMM operation for this batch and group
    scalar_t thread_sum = 0.0f;
    scalar_t thread_sum_sq = 0.0f;

    // Register cache for input values to reduce global memory accesses
    scalar_t input_cache[4];
    scalar_t weight_cache[4];

    for (int feature_offset = tid; feature_offset < features_per_group; feature_offset += blockDim.x) {
        int feature_idx = group_start + feature_offset;
        scalar_t result = bias[feature_idx];

        // Process input in chunks of 4 to improve memory access patterns
        for (int i = 0; i < in_features; i += 4) {
            if (i + 4 <= in_features) {
                // Vectorized load for better memory bandwidth
                loadVector4(input + batch_idx * in_features + i, input_cache, 0);
                loadVector4(weight + feature_idx * in_features + i, weight_cache, 0);

                #pragma unroll
                for (int j = 0; j < 4; ++j) {
                    result += input_cache[j] * weight_cache[j];
                }
            } else {
                // Handle remaining elements
                for (int j = 0; j < in_features - i; ++j) {
                    scalar_t input_val = input[batch_idx * in_features + i + j];
                    scalar_t weight_val = weight[feature_idx * in_features + i + j];
                    result += input_val * weight_val;
                }
            }
        }

        // Store result in shared memory for group norm calculation
        if (feature_offset < features_per_group) {
            group_output[feature_offset] = result;
            thread_sum += result;
            thread_sum_sq += result * result;
        }
    }

    // Store thread sums to shared memory
    group_sum[tid] = thread_sum;
    group_sum_sq[tid] = thread_sum_sq;

    __syncthreads();

    // Warp-level reduction for sum and sum of squares
    if (tid < 32) {
        // Load data from shared memory
        scalar_t warp_sum = 0.0f;
        scalar_t warp_sum_sq = 0.0f;

        for (int i = tid; i < blockDim.x; i += 32) {
            warp_sum += group_sum[i];
            warp_sum_sq += group_sum_sq[i];
        }

        // Perform warp-level reduction
        warp_sum = warpReduceSum(warp_sum);
        warp_sum_sq = warpReduceSum(warp_sum_sq);

        // First thread in the warp writes the result
        if (tid == 0) {
            group_sum[0] = warp_sum;
            group_sum_sq[0] = warp_sum_sq;
        }
    }

    __syncthreads();

    // Step 2: Group Normalization
    scalar_t mean = group_sum[0] / features_per_group;
    scalar_t var = (group_sum_sq[0] / features_per_group) - (mean * mean);
    var = max(var, 0.0f);  // Ensure variance is non-negative
    scalar_t inv_std = rsqrtf(var + eps);  // Use fast reciprocal square root

    // Normalize and apply gamma/beta for each feature in the group
    for (int feature_offset = tid; feature_offset < features_per_group; feature_offset += blockDim.x) {
        if (feature_offset < features_per_group) {
            int feature_idx = group_start + feature_offset;
            scalar_t normalized = (group_output[feature_offset] - mean) * inv_std;
            group_output[feature_offset] = normalized * gamma[feature_idx] + beta[feature_idx];
        }
    }

    __syncthreads();

    // Step 3: Find minimum value for this batch across all features in this group
    scalar_t local_min = INFINITY;
    for (int feature_offset = tid; feature_offset < features_per_group; feature_offset += blockDim.x) {
        if (feature_offset < features_per_group) {
            local_min = min(local_min, group_output[feature_offset]);
        }
    }

    // Store local min to shared memory
    shared_mem[tid] = local_min;

    __syncthreads();

    // Warp-level reduction for min
    if (tid < 32) {
        // Load data from shared memory
        scalar_t warp_min = INFINITY;

        for (int i = tid; i < blockDim.x; i += 32) {
            warp_min = min(warp_min, shared_mem[i]);
        }

        // Perform warp-level reduction
        warp_min = warpReduceMin(warp_min);

        // First thread in the warp writes the result
        if (tid == 0) {
            // Step 4: Add bias and write output
            scalar_t result = warp_min + final_bias[group_idx];
            output[batch_idx * num_groups + group_idx] = result;
        }
    }
}

std::vector<torch::Tensor> gemm_groupnorm_min_bias_cuda(
    torch::Tensor input,
    torch::Tensor weight,
    torch::Tensor bias,
    torch::Tensor gamma,
    torch::Tensor beta,
    torch::Tensor final_bias,
    int num_groups,
    float eps) {

    auto batch_size = input.size(0);
    auto in_features = input.size(1);
    auto out_features = weight.size(0);
    auto features_per_group = out_features / num_groups;

    auto output = torch::empty({batch_size, num_groups}, input.options());

    // Calculate optimal thread block size
    const int threads = 256;

    // Calculate shared memory size
    const int shared_mem_size = (2 * threads + features_per_group) * sizeof(float);

    const dim3 blocks(batch_size, num_groups);

    AT_DISPATCH_FLOATING_TYPES(input.type(), "gemm_groupnorm_min_bias_cuda", ([&] {
        gemm_groupnorm_min_bias_kernel<scalar_t><<<blocks, threads, shared_mem_size>>>(
            input.data_ptr<scalar_t>(),
            weight.data_ptr<scalar_t>(),
            bias.data_ptr<scalar_t>(),
            gamma.data_ptr<scalar_t>(),
            beta.data_ptr<scalar_t>(),
            final_bias.data_ptr<scalar_t>(),
            output.data_ptr<scalar_t>(),
            batch_size,
            in_features,
            out_features,
            num_groups,
            eps);
    }));

    return {output};
}

