class OptimizedConv2d(nn.Module):
    def __init__(self, in_channels, out_channels, kernel_size):
        super(OptimizedConv2d, self).__init__()
        self.in_channels = in_channels
        self.out_channels = out_channels
        self.kernel_size = kernel_size
        self.weight = nn.Parameter(torch.Tensor(out_channels, in_channels, kernel_size, kernel_size))
        self.bias = nn.Parameter(torch.Tensor(out_channels))
        self.reset_parameters()

    def reset_parameters(self):
        nn.init.kaiming_uniform_(self.weight, a=5**0.5)
        fan_in, _ = nn.init._calculate_fan_in_and_fan_out(self.weight)
        bound = 1 / (fan_in**0.5)
        nn.init.uniform_(self.bias, -bound, bound)

    def forward(self, x):
        # Ensure input is contiguous for better memory access patterns
        if not x.is_contiguous():
            x = x.contiguous()

        # Use custom CUDA kernel if available and if we're on GPU, otherwise fall back to PyTorch's conv2d
        if has_cuda_extension and self.kernel_size == 3 and x.is_cuda and self.in_channels == 3 and self.out_channels == 16:
            try:
                return conv2d_cuda.conv2d(x, self.weight, self.bias)
            except Exception:
                # Fall back to PyTorch implementation
                return F.conv2d(x, self.weight, self.bias)
        else:
            return F.conv2d(x, self.weight, self.bias)

