        // Fast approximate tanh using a polynomial approximation
        __device__ __forceinline__ float fast_tanh(float x) {
            // Clamp input for numerical stability
            float x_abs = fabsf(x);
            if (x_abs >= 4.0f) {
                return (x > 0.0f) ? 1.0f : -1.0f;
            }

            // Efficient polynomial approximation
            float x2 = x * x;
            float a = x * (135135.0f + x2 * (17325.0f + x2 * (378.0f + x2)));
            float b = 135135.0f + x2 * (62370.0f + x2 * (3150.0f + x2 * 28.0f));
            return a / b;
        }

        extern "C" __global__ void fused_post_processing(
            float* __restrict__ output,
            const float* __restrict__ input,
            const int numel,
            const float add_value,
            const float multiply_value)
        {
            // Calculate global thread position
            const int idx = blockIdx.x * blockDim.x + threadIdx.x;
            const int stride = blockDim.x * gridDim.x;

            // Constants for fast GELU approximation
            // GELU(x) â‰ˆ 0.5 * x * (1 + tanh(sqrt(2/Ï€) * (x + 0.044715 * xÂ³)))
            const float sqrt_2_over_pi = 0.7978845608028654f;
            const float coef = 0.044715f;

            // Use shared memory for frequently accessed constants
            __shared__ float s_add_value;
            __shared__ float s_multiply_value;

            if (threadIdx.x == 0) {
                s_add_value = add_value;
                s_multiply_value = multiply_value;
            }
            __syncthreads();

            // Process elements in grid-stride loop
            // Each thread processes 4 elements at a time when possible
            for (int i = idx * 4; i < numel; i += stride * 4) {
                // Check if we can process a full block of 4 elements
                if (i + 3 < numel) {
