                    if (((uintptr_t)&input[i] & 15) == 0 && ((uintptr_t)&output[i] & 15) == 0) {
                        // Aligned vectorized load
                        float4 vals = *reinterpret_cast<const float4*>(&input[i]);

                        // Add operation
                        vals.x += s_add_value;
                        vals.y += s_add_value;
                        vals.z += s_add_value;
                        vals.w += s_add_value;

                        // Min operation
                        vals.x = fminf(vals.x, 0.0f);
                        vals.y = fminf(vals.y, 0.0f);
                        vals.z = fminf(vals.z, 0.0f);
                        vals.w = fminf(vals.w, 0.0f);

                        // Optimized GELU approximation

                        // Process vals.x
                        float x = vals.x;
                        float x_squared = x * x;
                        float x_cubed = x * x_squared;
                        float inner = sqrt_2_over_pi * (x + coef * x_cubed);
                        vals.x = 0.5f * x * (1.0f + fast_tanh(inner));

                        // Process vals.y
                        x = vals.y;
                        x_squared = x * x;
                        x_cubed = x * x_squared;
                        inner = sqrt_2_over_pi * (x + coef * x_cubed);
                        vals.y = 0.5f * x * (1.0f + fast_tanh(inner));

