                        x_squared = x * x;
                        x_cubed = x * x_squared;
                        inner = sqrt_2_over_pi * (x + coef * x_cubed);
                        vals.w = 0.5f * x * (1.0f + fast_tanh(inner));

                        // Multiply operation
                        vals.x *= s_multiply_value;
                        vals.y *= s_multiply_value;
                        vals.z *= s_multiply_value;
                        vals.w *= s_multiply_value;

