                            // Add operation
                            val += s_add_value;

                            // Min operation
                            val = fminf(val, 0.0f);

                            // Fast GELU approximation
                            float x_squared = val * val;
                            float x_cubed = val * x_squared;
                            float inner = sqrt_2_over_pi * (val + coef * x_cubed);
                            val = 0.5f * val * (1.0f + fast_tanh(inner));

                            // Multiply operation
                            val *= s_multiply_value;

                            // Store result
                            output[i + j] = val;
                        }
                    }
                } else {
