                    // Ensure we have enough blocks to handle the data efficiently
                    blocks = std::min(blocks, (numel + threads * 4 - 1) / (threads * 4));
                    blocks = std::max(blocks, 1);  // Ensure at least one block

                    // Launch kernel
                    fused_post_processing<<<blocks, threads, 0, at::cuda::getCurrentCUDAStream()>>>(
                        output.data_ptr<float>(),
