// CUDA kernel for fused 1x1 convolution + batch norm + ReLU6
template <typename scalar_t>
__global__ void fused_conv1x1_bn_relu6_kernel(
    const scalar_t* __restrict__ input,
    const scalar_t* __restrict__ weight,
    const scalar_t* __restrict__ bias,
    scalar_t* __restrict__ output,
    const int batch_size,
    const int in_channels,
    const int out_channels,
    const int height,
    const int width) {

    // Calculate output position
    const int n = blockIdx.z;
    const int c_out = blockIdx.y * blockDim.y + threadIdx.y;
    const int h_idx = (blockIdx.x / ((width + 31) / 32)) * blockDim.x + threadIdx.x / ((width + 31) / 32);
    const int w_idx = (blockIdx.x % ((width + 31) / 32)) * 32 + threadIdx.x % 32;

    if (n >= batch_size || c_out >= out_channels || h_idx >= height || w_idx >= width)
        return;

    // Use shared memory to cache input data for the current position
    __shared__ scalar_t s_input[32][32];

    // Load input data into shared memory
    if (threadIdx.y < in_channels && threadIdx.x < 32) {
        s_input[threadIdx.y][threadIdx.x] = input[((n * in_channels + threadIdx.y) * height + h_idx) * width + w_idx];
    }
    __syncthreads();

    scalar_t sum = 0.0f;

    // 1x1 convolution (dot product across channels)
    for (int c_in = 0; c_in < in_channels; ++c_in) {
        const scalar_t val = input[((n * in_channels + c_in) * height + h_idx) * width + w_idx];
        const scalar_t w = weight[c_out * in_channels + c_in];
        sum += val * w;
    }

    // Add bias
    if (bias != nullptr) {
        sum += bias[c_out];
    }

    // ReLU6
    sum = (sum < 0) ? 0 : ((sum > 6) ? 6 : sum);

    // Write output
    output[((n * out_channels + c_out) * height + h_idx) * width + w_idx] = sum;
}

// CUDA kernel for fused depthwise convolution + batch norm + ReLU6
template <typename scalar_t>
__global__ void fused_depthwise_conv_bn_relu6_kernel(
    const scalar_t* __restrict__ input,
    const scalar_t* __restrict__ weight,
    const scalar_t* __restrict__ bias,
    scalar_t* __restrict__ output,
    const int batch_size,
    const int channels,
    const int in_height,
    const int in_width,
    const int out_height,
    const int out_width,
    const int kernel_size,
    const int stride,
    const int padding) {

    // Calculate output position
    const int n = blockIdx.z;
    const int c = blockIdx.y * blockDim.y + threadIdx.y;
    const int h_out = (blockIdx.x / ((out_width + 7) / 8)) * blockDim.x + threadIdx.x / ((out_width + 7) / 8);
    const int w_out = (blockIdx.x % ((out_width + 7) / 8)) * 8 + threadIdx.x % 8;

    if (n >= batch_size || c >= channels || h_out >= out_height || w_out >= out_width)
        return;

    scalar_t sum = 0.0f;

    // Depthwise convolution
    const int h_in_start = h_out * stride - padding;
    const int w_in_start = w_out * stride - padding;

    // Use shared memory for kernel weights
    __shared__ scalar_t s_weight[25];  // Max kernel size is 5x5

    // Load kernel weights into shared memory
    if (threadIdx.x < kernel_size * kernel_size && threadIdx.y == 0) {
        s_weight[threadIdx.x] = weight[c * kernel_size * kernel_size + threadIdx.x];
    }
    __syncthreads();

    for (int kh = 0; kh < kernel_size; ++kh) {
        const int h_in = h_in_start + kh;

        if (h_in >= 0 && h_in < in_height) {
            for (int kw = 0; kw < kernel_size; ++kw) {
                const int w_in = w_in_start + kw;

                if (w_in >= 0 && w_in < in_width) {
                    const scalar_t val = input[((n * channels + c) * in_height + h_in) * in_width + w_in];
                    const scalar_t w = s_weight[kh * kernel_size + kw];
                    sum += val * w;
                }
            }
        }
    }

    // Add bias
    if (bias != nullptr) {
        sum += bias[c];
    }

    // ReLU6
    sum = (sum < 0) ? 0 : ((sum > 6) ? 6 : sum);

    // Write output
    output[((n * channels + c) * out_height + h_out) * out_width + w_out] = sum;
}

// CUDA kernel for fused 1x1 convolution + batch norm
template <typename scalar_t>
__global__ void fused_conv1x1_bn_kernel(
