    const int w_idx = (blockIdx.x % ((width + 31) / 32)) * 32 + threadIdx.x % 32;

    if (n >= batch_size || c_out >= out_channels || h_idx >= height || w_idx >= width)
        return;

    scalar_t sum = 0.0f;

    // 1x1 convolution (dot product across channels)
    for (int c_in = 0; c_in < in_channels; ++c_in) {
        const scalar_t val = input[((n * in_channels + c_in) * height + h_idx) * width + w_idx];
        const scalar_t w = weight[c_out * in_channels + c_in];
        sum += val * w;
    }

    // Add bias
    if (bias != nullptr) {
        sum += bias[c_out];
    }

    // Write output
    output[((n * out_channels + c_out) * height + h_idx) * width + w_idx] = sum;
}

