class ModelNew(nn.Module):
    def __init__(self, in_channels, out_channels, kernel_size, stride, expand_ratio):
        """
        MBConv block implementation with optimized CUDA kernels.

        :param in_channels: Number of input channels.
        :param out_channels: Number of output channels.
        :param kernel_size: Kernel size for the depthwise convolution.
        :param stride: Stride for the depthwise convolution.
        :param expand_ratio: Expansion ratio for the intermediate channels.
        """
        super(ModelNew, self).__init__()

        self.use_residual = (stride == 1 and in_channels == out_channels)
        self.hidden_dim = in_channels * expand_ratio
        self.expand_ratio = expand_ratio
        self.kernel_size = kernel_size
        self.stride = stride
        self.padding = (kernel_size - 1) // 2

        # Flag to indicate if CUDA extension is available
        self.use_cuda_extension = has_cuda_extension

        # Expansion phase
        if expand_ratio != 1:
            self.expand_conv = nn.Conv2d(in_channels, self.hidden_dim, kernel_size=1, stride=1, padding=0, bias=False)
            self.expand_bn = nn.BatchNorm2d(self.hidden_dim)
            # Fused parameters for inference
            self.register_buffer('expand_fused_weight', torch.zeros_like(self.expand_conv.weight))
            self.register_buffer('expand_fused_bias', torch.zeros(self.hidden_dim))

        # Depthwise phase
        self.depthwise_conv = nn.Conv2d(
            self.hidden_dim, self.hidden_dim, kernel_size=kernel_size, stride=stride,
            padding=self.padding, groups=self.hidden_dim, bias=False
        )
        self.depthwise_bn = nn.BatchNorm2d(self.hidden_dim)
        # Fused parameters for inference
        self.register_buffer('depthwise_fused_weight', torch.zeros_like(self.depthwise_conv.weight))
        self.register_buffer('depthwise_fused_bias', torch.zeros(self.hidden_dim))

        # Projection phase
        self.project_conv = nn.Conv2d(self.hidden_dim, out_channels, kernel_size=1, stride=1, padding=0, bias=False)
        self.project_bn = nn.BatchNorm2d(out_channels)
        # Fused parameters for inference
        self.register_buffer('project_fused_weight', torch.zeros_like(self.project_conv.weight))
        self.register_buffer('project_fused_bias', torch.zeros(out_channels))

        # Initialize fused parameters
        self._fuse_bn_params()

    def _fuse_bn_params(self):
        """Fuse batch norm parameters into convolution weights and biases for inference"""
        # Set model to eval mode
        self.eval()

        # Fuse expand conv + bn
        if self.expand_ratio != 1:
            w = self.expand_conv.weight.data
            mean = self.expand_bn.running_mean
            var = self.expand_bn.running_var
            weight = self.expand_bn.weight
            bias = self.expand_bn.bias
            eps = self.expand_bn.eps

            # Fuse weights: w_fused = w * (bn_weight / sqrt(var + eps))
            std = torch.sqrt(var + eps)
            fused_weight = w * (weight / std).reshape(-1, 1, 1, 1)
            # Fuse bias: b_fused = bn_bias - bn_weight * mean / sqrt(var + eps)
            fused_bias = bias - weight * mean / std

            self.expand_fused_weight.copy_(fused_weight)
            self.expand_fused_bias.copy_(fused_bias)

        # Fuse depthwise conv + bn
        w = self.depthwise_conv.weight.data
        mean = self.depthwise_bn.running_mean
        var = self.depthwise_bn.running_var
        weight = self.depthwise_bn.weight
        bias = self.depthwise_bn.bias
        eps = self.depthwise_bn.eps

        # Fuse weights
        std = torch.sqrt(var + eps)
        fused_weight = w * (weight / std).reshape(-1, 1, 1, 1)
        # Fuse bias
        fused_bias = bias - weight * mean / std

        self.depthwise_fused_weight.copy_(fused_weight)
        self.depthwise_fused_bias.copy_(fused_bias)

        # Fuse project conv + bn
        w = self.project_conv.weight.data
        mean = self.project_bn.running_mean
        var = self.project_bn.running_var
        weight = self.project_bn.weight
        bias = self.project_bn.bias
        eps = self.project_bn.eps

        # Fuse weights
        std = torch.sqrt(var + eps)
        fused_weight = w * (weight / std).reshape(-1, 1, 1, 1)
        # Fuse bias
        fused_bias = bias - weight * mean / std

        self.project_fused_weight.copy_(fused_weight)
        self.project_fused_bias.copy_(fused_bias)

    def forward(self, x):
        """
        Forward pass of the MBConv block.

        :param x: The input tensor, shape (batch_size, in_channels, H, W)
        :return: The output tensor, shape (batch_size, out_channels, H', W')
        """
        identity = x

        # Ensure input is contiguous for optimal memory access
        if not x.is_contiguous():
            x = x.contiguous()

        # Use optimized CUDA kernels if available and in eval mode
        if self.use_cuda_extension and not self.training:
            # Expansion phase
            if self.expand_ratio != 1:
                x = mbconv_cuda.fused_conv1x1_bn_relu6(
                    x, self.expand_fused_weight, self.expand_fused_bias
                )

            # Depthwise phase
            x = mbconv_cuda.fused_depthwise_conv_bn_relu6(
                x, self.depthwise_fused_weight, self.depthwise_fused_bias,
                self.stride, self.padding
            )

            # Projection phase
            x = mbconv_cuda.fused_conv1x1_bn(
                x, self.project_fused_weight, self.project_fused_bias
            )

            # Residual connection
            if self.use_residual:
                x = x + identity

            return x

        # Fall back to PyTorch implementation if CUDA extension is not available
        # or during training
        if self.expand_ratio != 1:
            if self.training:
                x = self.expand_conv(x)
                x = self.expand_bn(x)
                x = F.relu6(x, inplace=True)
            else:
                # Use fused parameters during inference
                x = F.conv2d(x, self.expand_fused_weight, self.expand_fused_bias)
                x = F.relu6(x, inplace=True)

        if self.training:
            x = self.depthwise_conv(x)
            x = self.depthwise_bn(x)
            x = F.relu6(x, inplace=True)
        else:
            # Use fused parameters during inference
            x = F.conv2d(
                x, self.depthwise_fused_weight, self.depthwise_fused_bias,
                stride=self.stride, padding=self.padding, groups=self.hidden_dim
            )
            x = F.relu6(x, inplace=True)

        if self.training:
            x = self.project_conv(x)
            x = self.project_bn(x)
        else:
            # Use fused parameters during inference
            x = F.conv2d(x, self.project_fused_weight, self.project_fused_bias)

        # Residual connection
        if self.use_residual:
            x = x + identity

        return x

