        const int h = (idx % (height * width)) / width;
        const int w = idx % width;

        const int spatial_idx = h * width + w;
        const int batch_offset = b * channels * height * width;

        // Process all channels for this pixel
        for (int c = 0; c < channels; c++) {
            const int group_idx = c / channels_per_group;
            const int channel_in_group = c % channels_per_group;
            const int shuffled_c = channel_in_group * groups + group_idx;

            const int input_idx = batch_offset + (c * height * width) + spatial_idx;
            const int output_idx = batch_offset + (shuffled_c * height * width) + spatial_idx;

            output[output_idx] = input[input_idx];
        }
    }
}

torch::Tensor channel_shuffle_cuda_forward(
    torch::Tensor input,
    int groups) {

    const auto batch_size = input.size(0);
    const auto channels = input.size(1);
    const auto height = input.size(2);
    const auto width = input.size(3);
    const int channels_per_group = channels / groups;

    auto output = torch::empty_like(input);

    // Choose kernel based on tensor dimensions and groups
    if (groups == 3) {
        // For very large feature maps (224x224, 112x112)
        if (height >= 112) {
            const int threads = 256;
            const int blocks = min(65535, (batch_size * height * width + threads - 1) / threads);

            AT_DISPATCH_FLOATING_TYPES(input.scalar_type(), "channel_shuffle_g3_xlarge_kernel", ([&] {
                channel_shuffle_g3_xlarge_kernel<scalar_t><<<blocks, threads>>>(
                    input.data_ptr<scalar_t>(),
                    output.data_ptr<scalar_t>(),
                    batch_size,
                    channels,
                    height,
                    width
                );
            }));
        }
        // For large feature maps with vectorized operations (56x56)
        else if (height >= 56) {
            const int threads = 256;
            const int blocks = min(65535, (batch_size * height * width + threads - 1) / threads);

            AT_DISPATCH_FLOATING_TYPES(input.scalar_type(), "channel_shuffle_g3_vector_kernel", ([&] {
                channel_shuffle_g3_vector_kernel<scalar_t><<<blocks, threads>>>(
                    input.data_ptr<scalar_t>(),
                    output.data_ptr<scalar_t>(),
                    batch_size,
                    channels,
                    height,
                    width
                );
            }));
        }
        // For medium feature maps with shared memory (28x28)
        else if (height >= 28) {
            const dim3 threads(16, 16);
            const dim3 blocks(
                (width + threads.x - 1) / threads.x,
                (height + threads.y - 1) / threads.y,
                batch_size
            );

            // Calculate shared memory size with padding to avoid bank conflicts
            const int tile_size = 32;
            const int padded_tile_width = threads.x + (threads.x % 32 == 0 ? 0 : 32 - (threads.x % 32));
            const int smem_size = tile_size * padded_tile_width * sizeof(float);

            AT_DISPATCH_FLOATING_TYPES(input.scalar_type(), "channel_shuffle_g3_shared_kernel", ([&] {
                channel_shuffle_g3_shared_kernel<scalar_t><<<blocks, threads, smem_size>>>(
                    input.data_ptr<scalar_t>(),
                    output.data_ptr<scalar_t>(),
                    batch_size,
                    channels,
                    height,
                    width
                );
            }));
        }
        // For small feature maps (14x14, 7x7)
        else {
            const dim3 threads(min(32, width), min(32, height));  // Ensure thread count doesn't exceed feature map size
            const dim3 blocks(
                (width + threads.x - 1) / threads.x,
                (height + threads.y - 1) / threads.y,
                batch_size
            );

            AT_DISPATCH_FLOATING_TYPES(input.scalar_type(), "channel_shuffle_g3_small_kernel", ([&] {
                channel_shuffle_g3_small_kernel<scalar_t><<<blocks, threads>>>(
                    input.data_ptr<scalar_t>(),
                    output.data_ptr<scalar_t>(),
                    batch_size,
                    channels,
                    height,
                    width
                );
            }));
        }
    }
    // For any other group count, use general kernel
    else {
        const int threads = 256;
        const int blocks = min(65535, (batch_size * height * width + threads - 1) / threads);

        AT_DISPATCH_FLOATING_TYPES(input.scalar_type(), "channel_shuffle_general_kernel", ([&] {
            channel_shuffle_general_kernel<scalar_t><<<blocks, threads>>>(
                input.data_ptr<scalar_t>(),
                output.data_ptr<scalar_t>(),
                batch_size,
                channels,
                height,
                width,
                groups,
                channels_per_group
            );
        }));
    }

    // Check for CUDA errors
    CUDA_CHECK(cudaGetLastError());

    return output;
}

PYBIND11_MODULE(TORCH_EXTENSION_NAME, m) {
    m.def("forward", &channel_shuffle_cuda_forward, "Channel Shuffle forward (CUDA)");
}
